mapper.select:
SELECT BBS_SEQ , case when char_length( BBS_SBJ ) >= 20 then substring( BBS_SBJ from 1 for 20) || '...' else BBS_SBJ end as BBS_SBJ , REG_EMP_NO , case when REG_EMP_NM is null then '' else REG_EMP_NM end as REG_EMP_NM , (SELECT p.RET_VALUE FROM GET_FORMAT_DATE(REG_DATE) p ) as REG_DATE , (SELECT p.RET_VALUE FROM GET_FORMAT_HM(REG_HMS) p ) as REG_HMS , case when char_length( BBS_NOTE ) >= 20 then substring( BBS_NOTE from 1 for 20) || '...' else BBS_NOTE end as BBS_NOTE , (SELECT p.V_OUT_VALUE FROM GET_TBBS_FILE_EXIST_YN(BBS_SEQ) p) as fileYN , (SELECT p.V_OUT_VALUE FROM GET_TBBS_COMMENT_EXIST_YN(BBS_SEQ) p) as commentYN , (SELECT p.V_OUT_VALUE FROM GET_TBBS_MGT_READ_YN(BBS_SEQ, #{regEmpNo}) p) as newYN FROM TBBS_MGT WHERE 1=1
AND REG_DATE between #{startDate} and #{endDate}
AND BBS_SBJ CONTAINING #{searchText}
AND BBS_NOTE CONTAINING #{searchText}
AND REG_EMP_NM CONTAINING #{searchText}

mapper.delete:
    	delete from TBBS_MGT 
    	WHERE BBS_SEQ in
		
			 #{bbsSeq}

    	delete from TBBS_COMMENT
    	WHERE BBS_SEQ in
		
			 #{bbsSeq}

mapper.insert:
    	INSERT 
		INTO TBBS_MGT 
       	(BBS_SEQ, BBS_SBJ, REG_EMP_NO, REG_EMP_NM, REG_DATE, REG_HMS, BBS_NOTE)
       	VALUES 
       	( 
       	NEXT VALUE FOR SEQ_BBS
         ,#{bbsSbj}
         ,#{regEmpNo}
         ,#{regEmpNm}
         ,#{regDate} 
		 ,#{regHms}
		 ,#{bbsNote}
       	)
