mapper.select:
SELECT COUN_SEQ, CUST_NO, CUST_NM, TEL_NO, CALL_TYP_CD, (SELECT * FROM GET_CODE_NAME('1006',CALL_TYP_CD) p) as CALL_TYP_CDNM , ( SELECT p.RET_VALUE FROM GET_FORMAT_DATE(COUN_START_DATE) p ) as COUN_START_DATE, ( SELECT p.RET_VALUE FROM GET_FORMAT_HMS(COUN_START_HMS) p ) as COUN_START_HMS, ( SELECT p.RET_VALUE FROM GET_FORMAT_DATE(COUN_END_DATE) p ) as COUN_END_DATE, ( SELECT p.RET_VALUE FROM GET_FORMAT_HMS(COUN_END_HMS) p ) as COUN_END_HMS, COUN_CD, (SELECT * FROM GET_CODE_NAME('1009',COUN_CD) p) as COUN_CDNM , EMP_NO, EMP_NM, COUN_NOTE, CALLID FROM TCOUNSEL WHERE 1=1
AND COUN_START_DATE >= #{counStartDate}
AND COUN_END_DATE <= #{counEndDate}
AND CALL_TYP_CD = #{callTypCd}
AND EMP_NO = #{empNo}
AND COUN_CD = #{counCd}
AND COUN_NOTE like '%'||#{counNote}||'%'
AND COUN_SEQ = #{counSeq}
AND CUST_NO = #{custNo}
ORDER BY COUN_START_DATE DESC, COUN_START_HMS DESC

mapper.update:
	UPDATE TCOUNSEL
	SET COUN_CD = #{counCd},
		  COUN_NOTE = #{counNote}
    WHERE COUN_SEQ = #{counSeq}
