mapper.select:
SELECT FIRST 0 CUST_CD, (SELECT * FROM GET_CODE_NAME('1008',CUST_CD) p) as CUST_CDNM, CUST_NO, CUST_NM, TEL1_NO, TEL2_NO, TEL3_NO, FAX_NO, case when char_length( ADDR ) >= 20 then substring( ADDR from 1 for 20) || '...' else ADDR end AS ADDR, case when char_length( EMAILID ) >= 20 then substring( EMAILID from 1 for 20) || '...' else EMAILID end AS EMAILID, case when char_length( CUST_NOTE ) >= 20 then substring( CUST_NOTE from 1 for 20) || '...' else CUST_NOTE end AS CUST_NOTE FROM TCUSTOMER where 1=1
AND (TEL1_NO STARTING WITH #{telNo} or TEL2_NO STARTING WITH #{telNo} or TEL3_NO STARTING WITH #{telNo} )
AND CUST_NM STARTING WITH #{custNm}
AND CUST_NO = #{custNo}

SELECT CUST_CD, (SELECT * FROM GET_CODE_NAME('1008',CUST_CD) p) as CUST_CDNM, CUST_NO, CUST_NM, TEL1_NO, TEL2_NO, TEL3_NO, FAX_NO, ADDR, EMAILID, CUST_NOTE FROM TCUSTOMER where 1=1
AND (TEL1_NO STARTING WITH #{telNo} or TEL2_NO STARTING WITH #{telNo} or TEL3_NO STARTING WITH #{telNo} )
AND CUST_NM STARTING WITH #{custNm}
AND CUST_NO = #{custNo}

SELECT count(*) as existCnt
FROM TCUSTOMER
WHERE CUST_CD = '1001'
AND	TEL1_NO = #{tel1No}
AND CUST_NM = #{custNm}

mapper.delete:
DELETE FROM TCUSTOMER
WHERE CUST_NO in

	 #{custNo}
