mapper.select:
SELECT SMS_SEND_SEQ , CUST_NO , CUST_NM , SEND_TEL_NO , SEND_TYPE_CD , ( SELECT * FROM GET_CODE_NAME('1004', SEND_TYPE_CD) p ) as SEND_TYPE_CDNM , CATE_CD , ( SELECT * FROM GET_CODE_NAME('1003', CATE_CD) p ) as CATE_CDNM , ( SELECT * FROM GET_FORMAT_DATE(RESERVE_DATE) p ) as SEND_RES_DATE , ( SELECT * FROM GET_FORMAT_HMS(RESERVE_HMS) p ) as SEND_RES_HMS , ( SELECT * FROM GET_FORMAT_DATE(SEND_DATE) p ) as SEND_DATE , ( SELECT * FROM GET_FORMAT_HMS(SEND_HMS) p ) as SEND_HMS , SEND_CD , ( SELECT * FROM GET_CODE_NAME('1003', SEND_CD) p ) as SEND_CDNM , EMP_NO , EMP_NM , SEND_COMMENT FROM TSMS_SENDING WHERE 1=1
AND RESERVE_DATE between #{sendDate} and #{sendDate2}
AND SEND_TYPE_CD = #{sendTypCd}
AND EMP_NO = #{empNo}
ORDER BY SEND_RES_DATE DESC, SEND_RES_HMS DESC
