from ( SELECT START_DATE , EMP_NO, EMP_NM , SUM( CASE WHEN AGENT_STAT_CD = '1001' THEN AGENT_STAT_SEC ELSE 0 END ) AS AGT_STAT_1001_TIME , SUM( CASE WHEN AGENT_STAT_CD = '1002' THEN AGENT_STAT_SEC ELSE 0 END ) AS AGT_STAT_1002_TIME , SUM( CASE WHEN AGENT_STAT_CD = '1003' THEN AGENT_STAT_SEC ELSE 0 END ) AS AGT_STAT_1003_TIME , SUM( CASE WHEN AGENT_STAT_CD = '1004' THEN AGENT_STAT_SEC ELSE 0 END ) AS AGT_STAT_1004_TIME , SUM( CASE WHEN AGENT_STAT_CD = '1005' THEN AGENT_STAT_SEC ELSE 0 END ) AS AGT_STAT_1005_TIME FROM TAGENT_STAT WHERE 1=1
AND START_DATE >= #{leadStartDate}
AND START_DATE <= #{leadEndDate}
