AND REG_DATE >= #{leadStartDate}
AND REG_DATE <= #{leadEndDate}
AND EMP_NM = #{empNm}
group by REG_DATE , EMP_NO, EMP_NM ) B on a.START_DATE = b.REG_DATE AND A.EMP_NO = B.EMP_NO AND A.EMP_NM = B.EMP_NM

select case when a.START_DATE is null then b.REG_DATE else a.START_DATE end as START_DATE , case when a.EMP_NO is null then b.EMP_NO else a.EMP_NO end as EMP_NO , case when a.EMP_NM is null then b.EMP_NM else a.EMP_NM end as EMP_NM , case when b.TOT_IB_AG_TRANS_COUNT is null then 0 else TOT_IB_AG_TRANS_COUNT end as TOT_IB_AG_TRANS_COUNT , case when b.TOT_IB_AG_TRANS_TIME is null then 0 else TOT_IB_AG_TRANS_TIME end as TOT_IB_AG_TRANS_TIME , case when b.TOT_OUT_COUNT is null then 0 else TOT_OUT_COUNT end as TOT_OUT_COUNT , case when b.TOT_OUT_TIME is null then 0 else TOT_OUT_TIME end as TOT_OUT_TIME , case when b.TOT_EXT_COUNT is null then 0 else TOT_EXT_COUNT end as TOT_EXT_COUNT , case when B.TOT_EXT_TIME is null then 0 else TOT_EXT_TIME end as TOT_EXT_TIME , case when a.AGT_STAT_1001_TIME is null then 0 else AGT_STAT_1001_TIME end as AGT_STAT_1001_TIME , case when a.AGT_STAT_1002_TIME is null then 0 else AGT_STAT_1002_TIME end as AGT_STAT_1002_TIME , case when a.AGT_STAT_1003_TIME is null then 0 else AGT_STAT_1003_TIME end as AGT_STAT_1003_TIME , case when a.AGT_STAT_1004_TIME is null then 0 else AGT_STAT_1004_TIME end as AGT_STAT_1004_TIME , case when a.AGT_STAT_1005_TIME is null then 0 else AGT_STAT_1005_TIME end as AGT_STAT_1005_TIME
from ( SELECT substring(START_DATE from 1 for 6) as START_DATE , EMP_NO, EMP_NM , SUM( CASE WHEN AGENT_STAT_CD = '1001' THEN AGENT_STAT_SEC ELSE 0 END ) AS AGT_STAT_1001_TIME , SUM( CASE WHEN AGENT_STAT_CD = '1002' THEN AGENT_STAT_SEC ELSE 0 END ) AS AGT_STAT_1002_TIME , SUM( CASE WHEN AGENT_STAT_CD = '1003' THEN AGENT_STAT_SEC ELSE 0 END ) AS AGT_STAT_1003_TIME , SUM( CASE WHEN AGENT_STAT_CD = '1004' THEN AGENT_STAT_SEC ELSE 0 END ) AS AGT_STAT_1004_TIME , SUM( CASE WHEN AGENT_STAT_CD = '1005' THEN AGENT_STAT_SEC ELSE 0 END ) AS AGT_STAT_1005_TIME FROM TAGENT_STAT WHERE 1=1
AND START_DATE >= #{leadStartDate}
AND (START_DATE <= #{leadEndDate} OR START_DATE CONTAINING #{leadEndDate})
AND EMP_NM = #{empNm}
GROUP BY START_DATE , EMP_NO, EMP_NM ) A full join ( SELECT substring(REG_DATE from 1 for 6) as REG_DATE , EMP_NO, EMP_NM , SUM(case when CALL_TYP_CD = '1001' and AGENT_TRANS_YN = 'Y' then 1 else 0 end) as TOT_IB_AG_TRANS_COUNT , SUM(case when CALL_TYP_CD = '1001' and AGENT_TRANS_YN = 'Y' then ( CASE WHEN CALL_STAT_SEC IS NULL THEN 0 ELSE CALL_STAT_SEC END ) else 0 end) as TOT_IB_AG_TRANS_TIME , SUM(case when CALL_TYP_CD = '1002' then 1 else 0 end) as TOT_OUT_COUNT , SUM(case when CALL_TYP_CD = '1002' then ( CASE WHEN CALL_STAT_SEC IS NULL THEN 0 ELSE CALL_STAT_SEC END ) else 0 end) as TOT_OUT_TIME , SUM(case when CALL_TYP_CD = '1003' then 1 else 0 end) as TOT_EXT_COUNT , SUM(case when CALL_TYP_CD = '1003' then ( CASE WHEN CALL_STAT_SEC IS NULL THEN 0 ELSE CALL_STAT_SEC END ) else 0 end) as TOT_EXT_TIME FROM TCALL_STAT WHERE 1=1
AND REG_DATE >= #{leadStartDate}
AND (REG_DATE <= #{leadEndDate} OR REG_DATE CONTAINING #{leadEndDate})
AND EMP_NM = #{empNm}
group by REG_DATE , EMP_NO, EMP_NM ) B on a.START_DATE = b.REG_DATE AND A.EMP_NO = B.EMP_NO AND A.EMP_NM = B.EMP_NM

