mapper.insert:
insert into tagent_stat (
	AGENT_STAT_CD
	, EMP_NO
	, EMP_NM
	, START_DATE
	, START_HMS
	, END_DATE
	, END_HMS
	, AGENT_STAT_SEC
) values (
	#{agentStatCd}
	, #{empNo}
	, #{empNm}
	, #{startDate}
	, #{startHms}
	, #{endDate}
	, #{endHms}
	, #{agentStatSec}
)

mapper.select:
{call LOG_ADD ( #{agentStatCd, mode=IN, jdbcType=VARCHAR}
, #{sdate, mode=IN, jdbcType=VARCHAR}
, #{shms, mode=IN, jdbcType=VARCHAR}
, #{prevAgentStatCd, mode=IN, jdbcType=VARCHAR}
, #{pdate, mode=IN, jdbcType=VARCHAR}
, #{phms, mode=IN, jdbcType=VARCHAR}
, #{empNo, mode=IN, jdbcType=VARCHAR}
) }
