mapper.select:
SELECT FIRST 2000 CUST_CD, (SELECT * FROM GET_CODE_NAME('1008',CUST_CD) p) as CUST_CDNM, CUST_NO, CUST_NM, TEL1_NO, TEL2_NO, TEL3_NO, FAX_NO, case when char_length( ADDR ) >= 20 then substring( ADDR from 1 for 18) || '...' else ADDR end AS ADDR, case when char_length( EMAILID ) >= 15 then substring( EMAILID from 1 for 15) || '...' else EMAILID end AS EMAILID, case when char_length( CUST_NOTE ) >= 18 then substring( CUST_NOTE from 1 for 18) || '...' else CUST_NOTE end AS CUST_NOTE, GRADE_CD, (SELECT * FROM GET_CODE_NAME('1013',GRADE_CD) p) as GRADE_NM, CUST_TYP_CD, (SELECT * FROM GET_CODE_NAME('1014',CUST_TYP_CD) p) as CUST_TYP_NM, RECOGNITION_TYP_CD, (SELECT * FROM GET_CODE_NAME('1015',RECOGNITION_TYP_CD) p) as RECOGNITION_TYP_NM, SEX_CD, (SELECT * FROM GET_CODE_NAME('1016',SEX_CD) p) as SEX_NM, ( SELECT * FROM GET_FORMAT_DATE(BIRTH_DATE) p ) as BIRTH_DATE, ( SELECT * FROM GET_FORMAT_CO_REG_NO(CO_REG_NO) p ) as CO_REG_NO, ( SELECT * FROM GET_FORMAT_DATE(REG_DATE) p ) as REG_DATE, ( SELECT * FROM GET_FORMAT_DATE(LAST_COUN_DATE) p ) as LAST_COUN_DATE FROM TCUSTOMER where 1=1
AND (TEL1_NO STARTING WITH #{telNo} or TEL2_NO STARTING WITH #{telNo} or TEL3_NO STARTING WITH #{telNo} )
AND CUST_NM STARTING WITH #{custNm}
AND CO_REG_NO = #{coRegNo}
AND SEX_CD = #{sexCd}
AND REG_DATE between #{regDate} and #{regDate2}
AND LAST_COUN_DATE between #{lastCounDate} and #{lastCounDate2}
AND GRADE_CD = #{gradeCd}
AND CUST_TYP_CD = #{custTypCd}
AND RECOGNITION_TYP_CD = #{recogTypCd}

SELECT CUST_CD, (SELECT * FROM GET_CODE_NAME('1008',CUST_CD) p) as CUST_CDNM, CUST_NO, CUST_NM, TEL1_NO, TEL2_NO, TEL3_NO, FAX_NO, ADDR, EMAILID, CUST_NOTE, GRADE_CD, (SELECT * FROM GET_CODE_NAME('1013',GRADE_CD) p) as GRADE_NM, CUST_TYP_CD, (SELECT * FROM GET_CODE_NAME('1014',CUST_TYP_CD) p) as CUST_TYP_NM, RECOGNITION_TYP_CD, (SELECT * FROM GET_CODE_NAME('1015',RECOGNITION_TYP_CD) p) as RECOGNITION_TYP_NM, SEX_CD, (SELECT * FROM GET_CODE_NAME('1016',SEX_CD) p) as SEX_NM, ( SELECT * FROM GET_FORMAT_DATE(BIRTH_DATE) p ) as BIRTH_DATE, ( SELECT * FROM GET_FORMAT_CO_REG_NO(CO_REG_NO) p ) as CO_REG_NO, ( SELECT * FROM GET_FORMAT_DATE(REG_DATE) p ) as REG_DATE, ( SELECT * FROM GET_FORMAT_DATE(LAST_COUN_DATE) p ) as LAST_COUN_DATE FROM TCUSTOMER where 1=1
AND (TEL1_NO STARTING WITH #{telNo} or TEL2_NO STARTING WITH #{telNo} or TEL3_NO STARTING WITH #{telNo} )
AND CUST_NM STARTING WITH #{custNm}
AND CUST_NO = #{custNo}
AND CO_REG_NO = #{coRegNo}
AND SEX_CD = #{sexCd}
AND REG_DATE between #{regDate} and #{regDate2}
AND LAST_COUN_DATE between #{lastCounDate} and #{lastCounDate2}
AND GRADE_CD = #{gradeCd}
AND CUST_TYP_CD = #{custTypCd}
AND RECOGNITION_TYP_CD = #{recogTypCd}

SELECT count(*) as existCnt
FROM TCUSTOMER
WHERE CUST_CD = '1001'
AND	TEL1_NO = #{tel1No}
AND CUST_NM = #{custNm}

mapper.delete:
DELETE FROM TCUSTOMER
WHERE CUST_NO in

	 #{custNo}

mapper.insert:
    INSERT INTO TCUSTOMER
	(CUST_CD
	, CUST_NO
	, CUST_NM
	, TEL1_NO
	, TEL2_NO
	, TEL3_NO
	, EMAILID
	, FAX_NO
	, ADDR
	, CUST_NOTE
	, REG_DATE
	, SEX_CD
	, BIRTH_DATE
	, GRADE_CD
	, CUST_TYP_CD
	, RECOGNITION_TYP_CD) 
	VALUES 
	(#{custCd}
	, GEN_ID(SEQ_CUST_NO,1) 
	, #{custNm}
	, #{tel1No}
	, #{tel2No}
	, #{tel3No}
	, #{emailId}
	, #{faxNo}
	, #{addr}
	, #{custNote}
	,(SELECT p.RET_VALUE FROM GET_CHAR_DATE(0) p)
	, (SELECT p.V_SCD FROM GET_NAME_TO_CODE('1016', #{sexCd}) p)
	, #{birthDate}
	, (SELECT p.V_SCD FROM GET_NAME_TO_CODE('1013', #{gradeCd}) p)
	, (SELECT p.V_SCD FROM GET_NAME_TO_CODE('1014', #{custTypCd}) p) 
	, (SELECT p.V_SCD FROM GET_NAME_TO_CODE('1015', #{recogTypCd}) p)  
	)

